import React, { Component } from "react";
import * as THREE from "three";
class Prism extends Component {
  constructor(props) {
    super(props);

    let length = 8,
      width = 8;

    let shape = new THREE.Shape();
    shape.moveTo(-width, -length);
    shape.lineTo(-length, width);
    shape.lineTo(length, width);
    shape.lineTo(-width, -length);

    let extrudeSettings = {
      depth: 100
    };

    let prismGeometry = new THREE.ExtrudeGeometry(shape, extrudeSettings);
    let prismMaterial = new THREE.MeshLambertMaterial({
      color: Math.random() * 0xffffff

      // wireframe: true
    });
    let tri = new THREE.Mesh(prismGeometry, prismMaterial);
    tri.position.z = -140;
    tri.position.x = this.randomInRange(-45, 45);
    tri.position.y = 20;
    // this.scene.add(tri);
    this.tri = tri;
    return this.tri;
  }
  randomInRange(from, to) {
    let x = Math.random() * (to - from);
    return x + from;
  }
}

export default Prism;
