import React, { Component } from "react";
import ReactDOM from "react-dom";
import * as THREE from "three";
import Prism from "./prism";

class Scene extends Component {
  constructor(props) {
    super(props);

    this.start = this.start.bind(this);
    this.stop = this.stop.bind(this);
    this.animate = this.animate.bind(this);

    this.rotateConst = 0.01;
  }

  createDomeRoof() {
    let domeGeometry = new THREE.SphereGeometry(
      100,
      15,
      15,
      0,
      Math.PI,
      0,
      -Math.PI / 2
    ); //, Math.PI, Math.PI
    let domeTexture = new THREE.TextureLoader().load(
      "images/rough-white-grunge-brick-wall-texture.jpg"
    );
    domeTexture.wrapS = domeTexture.wrapT = THREE.RepeatWrapping;
    domeTexture.repeat.set(12, 12);
    let domeMaterial = new THREE.MeshBasicMaterial({ map: domeTexture });
    let dome = new THREE.Mesh(domeGeometry, domeMaterial);
    dome.material.side = THREE.DoubleSide;
    this.scene.add(dome);
  }

  createPrism() {
    this.scene.add(new prism());
    // let length = 22,
    //   width = 8;
    //
    // let shape = new THREE.Shape();
    // shape.moveTo(-width, -length);
    // shape.lineTo(-length, width);
    // shape.lineTo(length, width);
    // shape.lineTo(-width, -length);
    //
    // let extrudeSettings = {
    //   depth: 100
    // };
    //
    // let prismGeometry = new THREE.ExtrudeGeometry(shape, extrudeSettings);
    // let prismMaterial = new THREE.MeshLambertMaterial({
    //   color: 0x22a570
    //
    //   // wireframe: true
    // });
    // let tri = new THREE.Mesh(prismGeometry, prismMaterial);
    // tri.position.z = -140;
    // tri.position.x = -45;
    // tri.position.y = 20;
    // this.scene.add(tri);
    // this.tri = tri;
  }

  addFlooring() {
    let geometry = new THREE.PlaneGeometry(1000, 1000, 10, 10);
    geometry.rotateX(-Math.PI / 2);
    geometry.rotateY(-Math.PI / 2);
    let floorTexture = new THREE.TextureLoader().load(
      "images/wood-texture1.jpg"
    );
    floorTexture.wrapS = floorTexture.wrapT = THREE.RepeatWrapping;
    floorTexture.repeat.set(20, 20);
    let material = new THREE.MeshBasicMaterial({ map: floorTexture });
    let mesh = new THREE.Mesh(geometry, material);
    this.scene.add(mesh);
  }

  addLights() {
    let light = new THREE.DirectionalLight(0xffffff);
    light.position.y = 55;
    light.position.z = -5;
    let lightTarget = new THREE.Object3D();
    // lightTarget.position.x = -55;
    // lightTarget.position.z = -25;
    // lightTarget.position.y = 125;
    light.target = lightTarget;
    let lightHelper = new THREE.DirectionalLightHelper(light, 5, 0x000000);
    this.scene.add(lightHelper);
    this.scene.add(lightTarget);
    this.scene.add(light);

    // let ambLight = new THREE.AmbientLight(0xffffff);
    // ambLight.intensity = 0.1;
    // this.scene.add(ambLight);
  }

  componentDidMount() {
    const width = this.mount.clientWidth;
    const height = this.mount.clientHeight;

    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    const geometry = new THREE.BoxGeometry(20, 21, 1);
    const material = new THREE.MeshLambertMaterial({ color: "#433F81" });
    const cube = new THREE.Mesh(geometry, material);
    camera.position.y = 14;
    camera.position.x = 25;
    camera.position.z = 100;
    cube.position.x = 50;
    this.scene = scene;
    this.scene.add(cube);

    renderer.setClearColor("#000000");
    renderer.setSize(width, height);

    this.camera = camera;
    this.renderer = renderer;
    this.material = material;
    this.cube = cube;
    this.addLights();
    this.createPrism();
    this.createDomeRoof();
    this.addFlooring();

    this.mount.appendChild(this.renderer.domElement);
    this.start();
  }

  componentWillUnmount() {
    this.stop();
    this.mount.removeChild(this.renderer.domElement);
  }

  start() {
    if (!this.frameId) {
      this.frameId = requestAnimationFrame(this.animate);
    }
  }

  stop() {
    cancelAnimationFrame(this.frameId);
  }

  animate() {
    this.cube.rotation.x += 0.01;
    this.cube.rotation.y += 0.01;

    this.tri.rotateZ(this.rotateConst);
    this.renderScene();
    this.frameId = window.requestAnimationFrame(this.animate);
  }

  renderScene() {
    this.renderer.render(this.scene, this.camera);
  }

  render() {
    return (
      <div
        style={{ width: "100%", height: "1000px" }}
        ref={mount => {
          this.mount = mount;
        }}
      />
    );
  }
}
export default Scene;
// ReactDOM.render(<Scene />, document.querySelector("#root"));
